import{_ as o,o as t,c as e,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1105 Use of savings institution name, logo, or symbol for marketing purposes; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1105.md","filePath":"vacode/6.2-1105.md"}'),a={name:"vacode/6.2-1105.md"},n=i('<h1 id="§-6-2-1105-use-of-savings-institution-name-logo-or-symbol-for-marketing-purposes-penalty" tabindex="-1">§ 6.2-1105 Use of savings institution name, logo, or symbol for marketing purposes; penalty. <a class="header-anchor" href="#§-6-2-1105-use-of-savings-institution-name-logo-or-symbol-for-marketing-purposes-penalty" aria-label="Permalink to &quot;§ 6.2-1105 Use of savings institution name, logo, or symbol for marketing purposes; penalty.&quot;">​</a></h1><p>A. Except as provided in subsection B, no person shall use the name, logo, or symbol, or any combination thereof, of a savings institution, or any name, logo, or symbol, or any combination thereof, that is deceptively similar to the name, logo, or symbol of a savings institution, in marketing material provided to or solicitation of another person in a manner such that a reasonable person may believe that the marketing material or solicitation originated from or is endorsed by the savings institution or that the savings institution is responsible for the marketing material or solicitation.</p><p>B. This section shall not apply to (i) an affiliate or agent of the savings institution or (ii) a person who uses the name, logo, or symbol of a savings institution with the consent of the savings institution.</p><p>C. Any person violating the provisions of this section, either individually or as an interested party, is guilty of a Class 1 misdemeanor.</p><p>D. This section shall not affect the availability of any remedies otherwise available to a savings institution.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0240" target="_blank" rel="noreferrer">240</a>, § 6.1-194.93:1; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),s=[n];function r(l,p,m,g,h,c){return t(),e("div",null,s)}const d=o(a,[["render",r]]);export{u as __pageData,d as default};
