import{_ as t,o as n,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-1172 Accounts issued in name of minor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1172.md","filePath":"vacode/6.2-1172.md"}'),i={name:"vacode/6.2-1172.md"},r=e("h1",{id:"§-6-2-1172-accounts-issued-in-name-of-minor",tabindex:"-1"},[o("§ 6.2-1172 Accounts issued in name of minor. "),e("a",{class:"header-anchor",href:"#§-6-2-1172-accounts-issued-in-name-of-minor","aria-label":'Permalink to "§ 6.2-1172 Accounts issued in name of minor."'},"​")],-1),s=e("p",null,"A savings institution may issue accounts to a minor as sole and absolute owner of the account. With respect to any such account, a savings institution may (i) receive deposits by or for the minor owner, (ii) pay withdrawals, (iii) accept pledges to the association, and (iv) act in any other manner with respect to such accounts on the order of the minor owner. Any payment or delivery of funds from such account to its owner, or payment of a check or other written order for withdrawal signed by its minor owner, shall be a valid and sufficient release and discharge of the institution for any payment or delivery so made. The parent or guardian of the minor owner shall not in his capacity as parent or guardian have the power to withdraw or transfer funds in any such account unless the minor has given written notice to the association to accept the signature of such parent or guardian.",-1),c=e("p",null,[o("Code 1950, § 6-201.23; 1960, c. 402; 1966, c. 584, § 6.1-150; 1972, c. 796, § 6.1-195.25; 1985, c. 425, § 6.1-194.55; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),o(".")],-1),d=[r,s,c];function h(u,l,m,f,p,_){return n(),a("div",null,d)}const w=t(i,[["render",h]]);export{v as __pageData,w as default};
