import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1307 Use of credit union name, logo, or symbol for marketing purposes; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1307.md","filePath":"vacode/6.2-1307.md"}'),i={name:"vacode/6.2-1307.md"},n=a('<h1 id="§-6-2-1307-use-of-credit-union-name-logo-or-symbol-for-marketing-purposes-penalty" tabindex="-1">§ 6.2-1307 Use of credit union name, logo, or symbol for marketing purposes; penalty. <a class="header-anchor" href="#§-6-2-1307-use-of-credit-union-name-logo-or-symbol-for-marketing-purposes-penalty" aria-label="Permalink to &quot;§ 6.2-1307 Use of credit union name, logo, or symbol for marketing purposes; penalty.&quot;">​</a></h1><p>A. Except as provided in subsection B, no person shall use the name, logo, or symbol, or any combination thereof, of a credit union, or any name, logo, or symbol, or any combination thereof, that is deceptively similar to the name, logo, or symbol of a credit union, in marketing material provided to or solicitation of another person in a manner such that a reasonable person may believe that the marketing material or solicitation originated from or is endorsed by the credit union or that the credit union is responsible for the marketing material or solicitation.</p><p>B. This section shall not apply to (i) an affiliate or agent of the credit union or (ii) a person who uses the name, logo, or symbol of a credit union with the consent of the credit union.</p><p>C. Any person violating the provisions of this section, either individually or as an interested party, is guilty of a Class 1 misdemeanor.</p><p>D. This section shall not affect the availability of any remedies otherwise available to a credit union.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0240" target="_blank" rel="noreferrer">240</a>, § 6.1-225.65; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),r=[n];function s(l,c,p,m,d,h){return o(),t("div",null,r)}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
