import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1320 Incorporation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1320.md","filePath":"vacode/6.2-1320.md"}'),a={name:"vacode/6.2-1320.md"},i=t('<h1 id="§-6-2-1320-incorporation" tabindex="-1">§ 6.2-1320 Incorporation. <a class="header-anchor" href="#§-6-2-1320-incorporation" aria-label="Permalink to &quot;§ 6.2-1320 Incorporation.&quot;">​</a></h1><p>A. Five or more residents of the Commonwealth who are of legal age and share a common bond referred to in subsection B of § <a href="http://law.lis.virginia.gov/vacode/6.2-1327/" target="_blank" rel="noreferrer">6.2-1327</a> may establish, pursuant to the provisions of the Virginia Nonstock Corporation Act (§ <a href="http://law.lis.virginia.gov/vacode/13.1-801/" target="_blank" rel="noreferrer">13.1-801</a> et seq.), a corporation for the purpose of conducting business as a credit union as provided in this chapter. Every corporation organized under this chapter shall include in the corporate name the words &quot;credit union&quot; as well as some other distinguishing word or words.</p><p>B. Credit unions incorporated pursuant to this chapter shall be subject to the provisions of the Virginia Nonstock Corporation Act (§ <a href="http://law.lis.virginia.gov/vacode/13.1-801/" target="_blank" rel="noreferrer">13.1-801</a> et seq.), except as may otherwise be provided in this chapter.</p><p>Code 1950, § 6-204; 1956, c. 433; 1958, c. 139; 1966, c. 584, § 6.1-196; 1968, c. 560; 1990, c. 373, § 6.1-225.13; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[i];function s(c,p,h,d,l,_){return o(),r("div",null,n)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
