import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1322 Contents of bylaws; amendments to bylaws generally.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1322.md","filePath":"vacode/6.2-1322.md"}'),n={name:"vacode/6.2-1322.md"},o=i('<h1 id="§-6-2-1322-contents-of-bylaws-amendments-to-bylaws-generally" tabindex="-1">§ 6.2-1322 Contents of bylaws; amendments to bylaws generally. <a class="header-anchor" href="#§-6-2-1322-contents-of-bylaws-amendments-to-bylaws-generally" aria-label="Permalink to &quot;§ 6.2-1322 Contents of bylaws; amendments to bylaws generally.&quot;">​</a></h1><p>The bylaws of a credit union shall specify:</p><ol><li><p>The name of the credit union;</p></li><li><p>The purpose for which it was formed;</p></li><li><p>The time of the annual meeting of the members of the credit union, or a provision that the board of directors may set the time for the meeting. Such a meeting shall be held each calendar year. Notice of all meetings shall be given in a manner prescribed in the bylaws, subject to compliance with § <a href="http://law.lis.virginia.gov/vacode/13.1-842/" target="_blank" rel="noreferrer">13.1-842</a>;</p></li><li><p>The number, authority, and the duties of the directors and the authority, duties, and maximum compensation of all officers;</p></li><li><p>The conditions and qualifications for membership;</p></li><li><p>The number of members of the credit committee, if any, and of the supervisory committee, with their respective authorities and duties;</p></li><li><p>The conditions upon which shares may be issued, transferred, or withdrawn;</p></li><li><p>The conditions upon which loans may be made and repaid;</p></li><li><p>The manner of effecting the forfeiture of a member&#39;s shares when a member&#39;s share account balance is below the amount established by the bylaws and remains below such amount for a period of two years;</p></li><li><p>The manner in which dividends shall be determined and paid out;</p></li><li><p>The manner in which remaining assets are to be distributed in the event of dissolution after all distributions required by subdivision A 1 of § <a href="http://law.lis.virginia.gov/vacode/13.1-907/" target="_blank" rel="noreferrer">13.1-907</a> have been made; and</p></li><li><p>The manner in which bylaws may be amended.</p></li></ol><p>Code 1950, § 6-207; 1956, c. 90; 1966, c. 584, § 6.1-198; 1968, c. 560; 1980, c. 181; 1983, c. 300; 1990, c. 373, § 6.1-225.15; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[o];function s(l,h,d,c,m,p){return a(),t("div",null,r)}const u=e(n,[["render",s]]);export{b as __pageData,u as default};
