import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-1338 Duties and additional powers of corporation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1338.md","filePath":"vacode/6.2-1338.md"}'),n={name:"vacode/6.2-1338.md"},a=t('<h1 id="§-6-2-1338-duties-and-additional-powers-of-corporation" tabindex="-1">§ 6.2-1338 Duties and additional powers of corporation. <a class="header-anchor" href="#§-6-2-1338-duties-and-additional-powers-of-corporation" aria-label="Permalink to &quot;§ 6.2-1338 Duties and additional powers of corporation.&quot;">​</a></h1><p>A corporation shall have the following powers in addition to those otherwise provided:</p><ol><li><p>To advance funds to aid member credit unions to operate and meet liquidity requirements;</p></li><li><p>To assist in the merger, consolidation, and liquidation of credit unions;</p></li><li><p>To receive by assignment or purchase from member credit unions property of any nature owned by them;</p></li><li><p>Upon written direction of the Commission, to assume control of the property and business of any member credit union and to operate the credit union in accordance with the directions of the Commission; and</p></li><li><p>To invest its funds in (i) bonds, notes, or securities of the Commonwealth and of the federal government, and their agencies; (ii) deposits in banks doing business in the Commonwealth; (iii) deposits in any savings institution doing business in the Commonwealth the accounts of which are insured by the Federal Deposit Insurance Corporation or other federal insurance agency; and (iv) such other investments as are deemed prudent by the directors and are approved by the Commission.</p></li></ol><p>1974, c. 587, § 6.1-226.8; 1990, c. 3; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[a];function s(d,c,p,l,h,m){return o(),i("div",null,r)}const _=e