import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-1347 Conversion of state credit union to federal credit union.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1347.md","filePath":"vacode/6.2-1347.md"}'),n={name:"vacode/6.2-1347.md"},a=i('<h1 id="§-6-2-1347-conversion-of-state-credit-union-to-federal-credit-union" tabindex="-1">§ 6.2-1347 Conversion of state credit union to federal credit union. <a class="header-anchor" href="#§-6-2-1347-conversion-of-state-credit-union-to-federal-credit-union" aria-label="Permalink to &quot;§ 6.2-1347 Conversion of state credit union to federal credit union.&quot;">​</a></h1><p>A state credit union may convert to a federal credit union by the following procedure:</p><ol><li><p>At any meeting of the members called and held in accordance with the Virginia Nonstock Corporation Act (§ <a href="http://law.lis.virginia.gov/vacode/13.1-801/" target="_blank" rel="noreferrer">13.1-801</a> et seq.) to consider such action, the members, by an affirmative vote of those holding and voting two-thirds of the votes present in person or by proxy, may resolve to convert the credit union into a federal credit union;</p></li><li><p>A copy of the minutes of the meeting duly certified by the authorized officer of the credit union shall be transmitted to the Commission;</p></li><li><p>The state credit union shall take such action as is necessary under § 125 (b) of the Federal Credit Union Act (12 U.S.C. § 1771), as it now exists or may hereafter be amended, to make it a federal credit union;</p></li><li><p>It shall file with the Commission a certified copy of the organization certificate approved by the National Credit Union Administration Board; and</p></li><li><p>Upon receipt of the organization certificate the state credit union shall become a federal credit union which shall be vested with all of the assets and shall continue to be responsible for all of the obligations of the state credit union to the same extent as though the conversion had not taken place.</p></li></ol><p>1984, c. 352, § 6.1-200.7; 1990, c. 373, § 6.1-225.30; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[a];function c(s,d,l,h,f,p){return t(),o("div",null,r)}const _=e(n,[["render",c]]);export{m as __pageData,_ as default};
