import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 6.2-1352 Compensation of officials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1352.md","filePath":"vacode/6.2-1352.md"}'),a={name:"vacode/6.2-1352.md"},r=t('<h1 id="§-6-2-1352-compensation-of-officials" tabindex="-1">§ 6.2-1352 Compensation of officials. <a class="header-anchor" href="#§-6-2-1352-compensation-of-officials" aria-label="Permalink to &quot;§ 6.2-1352 Compensation of officials.&quot;">​</a></h1><p>A. Compensation of members of the board of directors and members of the credit and supervisory committees shall be determined by a written policy approved by the board of directors, provided that annual compensation for an individual member does not exceed $6,000.</p><p>B. Health, accident, and term life insurance protection for a director or committee member shall not be considered compensation.</p><p>C. Directors and committee members, while on official business of the credit union, may be reimbursed for expenses consistent with Internal Revenue Service guidelines.</p><p>Code 1950, § 6-220; 1964, c. 75; 1966, c. 584, § 6.1-211; 1968, c. 560; 1982, c. 572; 1983, c. 158; 1984, c. 354; 1988, c. 275; 1990, c. 373, § 6.1-225.35; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0325" target="_blank" rel="noreferrer">325</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0262" target="_blank" rel="noreferrer">262</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>.</p>',5),n=[r];function c(s,l,f,d,p,m){return o(),i("div",null,n)}const b=e(a,[["render",c]]);export{h as __pageData,b as default};
