import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1355 Supervisory committee; suspension and removal of officials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1355.md","filePath":"vacode/6.2-1355.md"}'),s={name:"vacode/6.2-1355.md"},r=t('<h1 id="§-6-2-1355-supervisory-committee-suspension-and-removal-of-officials" tabindex="-1">§ 6.2-1355 Supervisory committee; suspension and removal of officials. <a class="header-anchor" href="#§-6-2-1355-supervisory-committee-suspension-and-removal-of-officials" aria-label="Permalink to &quot;§ 6.2-1355 Supervisory committee; suspension and removal of officials.&quot;">​</a></h1><p>The supervisory committee:</p><ol><li><p>Shall make or cause to be made an annual audit of the credit union, and shall submit a report of that audit to the board of directors and a summary of the report to the members at the next annual meeting of the credit union;</p></li><li><p>Shall make or cause to be made such supplementary audits and verification of members&#39; accounts as it deems necessary or as may be ordered by the board of directors, and shall submit such report to the board of directors; and</p></li><li><p>May by unanimous vote suspend any officer of the credit union or any member of the credit committee or of the board of directors, until the next meeting of members, which shall be held not less than 10 nor more than 25 days after any such suspension, at which meeting such suspension shall be acted upon by the members.</p></li></ol><p>Code 1950, § 6-222; 1966, c. 584, § 6.1-213; 1974, c. 106; 1982, c. 574; 1990, c. 373, § 6.1-225.38; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),i=[r];function n(c,m,d,l,h,u){return o(),a("div",null,i)}const _=e(s,[["render",n]]);export{p as __pageData,_ as default};
