import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1368 Accounts of fiduciaries.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1368.md","filePath":"vacode/6.2-1368.md"}'),i={name:"vacode/6.2-1368.md"},o=t('<h1 id="§-6-2-1368-accounts-of-fiduciaries" tabindex="-1">§ 6.2-1368 Accounts of fiduciaries. <a class="header-anchor" href="#§-6-2-1368-accounts-of-fiduciaries" aria-label="Permalink to &quot;§ 6.2-1368 Accounts of fiduciaries.&quot;">​</a></h1><p>A credit union may issue shares and maintain share accounts in the name of any person or entity eligible for membership in such credit union pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-1327/" target="_blank" rel="noreferrer">6.2-1327</a> as administrator, executor, custodian, conservator, guardian, trustee, or other fiduciary for a named beneficiary. The payment of funds from any such account pursuant to a share draft or other written order of withdrawal signed by the fiduciary, the delivery of funds in such account to such fiduciary, or a receipt signed by any such fiduciary with regard to the payment of funds from such account, shall be valid and sufficient release and discharge of the credit union for the payment or delivery so made.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>, § 6.1-225.50:1; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),n=[o];function c(s,d,f,u,h,l){return a(),r("div",null,n)}const m=e(i,[["render",c]]);export{p as __pageData,m as default};
