import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1409 Prepayment by borrower from association; rebates for unearned interest; prepayment penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1409.md","filePath":"vacode/6.2-1409.md"}'),o={name:"vacode/6.2-1409.md"},n=r('<h1 id="§-6-2-1409-prepayment-by-borrower-from-association-rebates-for-unearned-interest-prepayment-penalty" tabindex="-1">§ 6.2-1409 Prepayment by borrower from association; rebates for unearned interest; prepayment penalty. <a class="header-anchor" href="#§-6-2-1409-prepayment-by-borrower-from-association-rebates-for-unearned-interest-prepayment-penalty" aria-label="Permalink to &quot;§ 6.2-1409 Prepayment by borrower from association; rebates for unearned interest; prepayment penalty.&quot;">​</a></h1><p>A. Any individual borrowing from an association shall have the right to anticipate payment of his debt at any time.</p><p>B. If interest has been added to the face amount of the note, the borrower shall have the right, upon prepayment of the debt, to receive a rebate by way of credit for any unearned interest. The rebate shall be computed:</p><ol><li><p>On loans (i) with an initial maturity and corresponding amortization period of 61 months or less and (ii) payable in equal periodic installments, in accordance with the Rule of 78 as illustrated in § <a href="http://law.lis.virginia.gov/vacode/6.2-403/" target="_blank" rel="noreferrer">6.2-403</a> or by using any other method that is at least as favorable to such borrower; and</p></li><li><p>On other loans, under a method at least as favorable to the borrower as the actuarial method.</p></li></ol><p>C. An association may charge a prepayment penalty not to exceed two percent of the amount of the prepayment, provided such prepayment penalty, including the percent thereof, is set forth in the contract of indebtedness and is disclosed to the borrower pursuant to applicable federal interest disclosure laws.</p><p>1987, c. 622, § 6.1-330.84; 1990, c. 338; 1991, c. 171; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),i=[n];function s(p,l,c,d,h,m){return t(),a("div",null,i)}const y=e(o,[["render",s]]);export{b as __pageData,y as default};
