import{_ as e,o as i,c as s,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1502 Certain persons ineligible as licensees; exception for subsidiaries.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1502.md","filePath":"vacode/6.2-1502.md"}'),n={name:"vacode/6.2-1502.md"},t=a('<h1 id="§-6-2-1502-certain-persons-ineligible-as-licensees-exception-for-subsidiaries" tabindex="-1">§ 6.2-1502 Certain persons ineligible as licensees; exception for subsidiaries. <a class="header-anchor" href="#§-6-2-1502-certain-persons-ineligible-as-licensees-exception-for-subsidiaries" aria-label="Permalink to &quot;§ 6.2-1502 Certain persons ineligible as licensees; exception for subsidiaries.&quot;">​</a></h1><p>A. No person doing business under the authority of any law of the Commonwealth or of the United States relating to banks, savings institutions, trust companies, building and loan associations, industrial loan associations, or credit unions shall be eligible for licensure under this chapter.</p><p>B. Nothing contained in subsection A or any other section of this title shall be construed to prevent a subsidiary of a bank or savings institution from becoming a licensee under this chapter. A licensee that is a subsidiary or affiliate of a bank or savings institution shall be governed by the provisions of this chapter, and all regulations adopted hereunder, as fully as if such licensee were not such a subsidiary or affiliate.</p><p>Code 1950, § 6-280; 1966, c. 584, § 6.1-250; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0016" target="_blank" rel="noreferrer">16</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0192" target="_blank" rel="noreferrer">192</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[t];function o(l,c,d,p,h,f){return i(),s("div",null,r)}const g=e(n,[["render",o]]);e