import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1508 Notices to Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1508.md","filePath":"vacode/6.2-1508.md"}'),a={name:"vacode/6.2-1508.md"},r=t('<h1 id="§-6-2-1508-notices-to-commission" tabindex="-1">§ 6.2-1508 Notices to Commission. <a class="header-anchor" href="#§-6-2-1508-notices-to-commission" aria-label="Permalink to &quot;§ 6.2-1508 Notices to Commission.&quot;">​</a></h1><p>A. After receiving approval to conduct business at an additional location pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/6.2-1507/" target="_blank" rel="noreferrer">6.2-1507</a> or providing notice of relocation pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-1519/" target="_blank" rel="noreferrer">6.2-1519</a>, a licensee shall give written notice to the Commissioner within 10 days of the commencement of business at the additional location or relocated place of business.</p><p>B. Every licensee shall notify the Commissioner, in writing, within 10 days of (i) the closing of any business location, and (ii) the name, address and position of each new senior officer, member, partner, or director. The licensee also shall provide such other information with respect to any such event as the Commissioner may reasonably require.</p><p>1982, c. 79, § 6.1-256.1; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[r];function s(c,l,h,d,f,p){return o(),i("div",null,n)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
