import{_ as e,o as n,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1509 Contents, posting, transfer, and duration of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1509.md","filePath":"vacode/6.2-1509.md"}'),i={name:"vacode/6.2-1509.md"},s=a('<h1 id="§-6-2-1509-contents-posting-transfer-and-duration-of-license" tabindex="-1">§ 6.2-1509 Contents, posting, transfer, and duration of license. <a class="header-anchor" href="#§-6-2-1509-contents-posting-transfer-and-duration-of-license" aria-label="Permalink to &quot;§ 6.2-1509 Contents, posting, transfer, and duration of license.&quot;">​</a></h1><p>A. Each license shall contain:</p><ol><li><p>The address at which the business is to be conducted;</p></li><li><p>The full name of the licensee or, if the licensee is a partnership or association, the names of the partners or members; and</p></li><li><p>If the licensee is a corporation, the date and place of incorporation.</p></li></ol><p>B. The licensee shall post the license prominently in each approved place of business of the licensee. The licensee shall prominently disclose on its website the license number assigned by the Commission to the licensee.</p><p>C. The license shall not be transferable or assignable.</p><p>D. Each license shall remain in full force and effect until surrendered, revoked, or suspended as provided by this chapter or by lawful order of the Commission.</p><p>Code 1950, §§ 6-288, 6-289; 1966, c. 584, §§ 6.1-258, 6.1-259; 1980, c. 211; 1982, c. 609; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',7),o=[s];function r(l,c,p,d,h,f){return n(),t("div",null,o)}const g