import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-1519 Changing place of business.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1519.md","filePath":"vacode/6.2-1519.md"}'),o={name:"vacode/6.2-1519.md"},t=n('<h1 id="§-6-2-1519-changing-place-of-business" tabindex="-1">§ 6.2-1519 Changing place of business. <a class="header-anchor" href="#§-6-2-1519-changing-place-of-business" aria-label="Permalink to &quot;§ 6.2-1519 Changing place of business.&quot;">​</a></h1><p>A. A licensee may change its place of business to a different location in the Commonwealth if the new location is:</p><ol><li><p>Within the original locality; or</p></li><li><p>From the original locality to a location in a contiguous locality.</p></li></ol><p>B. A licensee shall notify the Commission of a change in the place of business within 10 days of such relocation. Upon receipt of the notification, the Commission shall issue and deliver to the licensee an amended license covering the new location if it finds that the change in the place of business meets one of the criteria listed in subsection A. Each notice of change of location under this section shall be accompanied by a fee of $250.</p><p>1982, c. 78, § 6.1-269.1; 1988, c. 147; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0192" target="_blank" rel="noreferrer">192</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),s=[t];function c(l,r,h,f,p,_){return i(),a("div",null,s)}const u=e(o,[["render",c]]);export{g as __pageData,u as default};
