import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1529 Collection of loans made outside Commonwealth.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1529.md","filePath":"vacode/6.2-1529.md"}'),n={name:"vacode/6.2-1529.md"},r=a('<h1 id="§-6-2-1529-collection-of-loans-made-outside-commonwealth" tabindex="-1">§ 6.2-1529 Collection of loans made outside Commonwealth. <a class="header-anchor" href="#§-6-2-1529-collection-of-loans-made-outside-commonwealth" aria-label="Permalink to &quot;§ 6.2-1529 Collection of loans made outside Commonwealth.&quot;">​</a></h1><p>No loan made outside the Commonwealth for which greater rates of interest, consideration or charges than are permitted by the law applicable to such loan in the state in which the loan was made, have been charged, contracted for, or received shall be collected in the Commonwealth. Every person in any way participating in an effort to enforce the collection of such loan in the Commonwealth shall be subject to the provisions of this chapter.</p><p>Code 1950, § 6-320; 1956, c. 71; 1966, c. 584, § 6.1-291; 1968, c. 489; 1974, c. 371; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0308" target="_blank" rel="noreferrer">308</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),i=[r];function l(c,s,h,d,m,_){return o(),t("div",null,i)}const g=e(n,[["render",l]]);export{p as __pageData,g as default};
