import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-1602 Persons exempt from chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1602.md","filePath":"vacode/6.2-1602.md"}'),i={name:"vacode/6.2-1602.md"},o=t('<h1 id="§-6-2-1602-persons-exempt-from-chapter" tabindex="-1">§ 6.2-1602 Persons exempt from chapter. <a class="header-anchor" href="#§-6-2-1602-persons-exempt-from-chapter" aria-label="Permalink to &quot;§ 6.2-1602 Persons exempt from chapter.&quot;">​</a></h1><p>The following shall be exempt from the licensing and other provisions of this chapter:</p><ol><li><p>Lenders making three or fewer mortgage loans in any period of 12 consecutive months;</p></li><li><p>Any person subject to the general supervision of or subject to examination by the Commissioner pursuant to Chapter 7 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-700/" target="_blank" rel="noreferrer">6.2-700</a> et seq.), 8 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-800/" target="_blank" rel="noreferrer">6.2-800</a> et seq.), 11 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-1100/" target="_blank" rel="noreferrer">6.2-1100</a> et seq.), 13 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-1300/" target="_blank" rel="noreferrer">6.2-1300</a> et seq.), or 14 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-1400/" target="_blank" rel="noreferrer">6.2-1400</a> et seq.);</p></li><li><p>Any lender authorized to engage in business as a bank, savings institution, or credit union under the laws of the United States or any state, and subsidiaries and affiliates of such entities which lender, subsidiary or affiliate is subject to the general supervision or regulation of or subject to audit or examination by a regulatory body or agency of the United States or any state;</p></li><li><p>Nonprofit corporations making mortgage loans to promote home ownership or improvements for the disadvantaged;</p></li><li><p>Agencies of the federal government, or any state or municipal government, or any quasi-governmental agency making or brokering mortgage loans under the specific authority of the laws of any state or the United States;</p></li><li><p>Persons acting as fiduciaries with respect to any employee pension benefit plan qualified under the Internal Revenue Code who make mortgage loans solely to plan participants from plan assets;</p></li><li><p>Any insurance company;</p></li><li><p>Persons licensed by the Commonwealth as attorneys, real estate brokers, or real estate salesmen, not actively and principally engaged in negotiating, placing or finding mortgage loans, when rendering services as an attorney, real estate broker or real estate salesman; however, a real estate broker or real estate salesman who receives any fee, commission, kickback, rebate or other payment for directly or indirectly negotiating, placing or finding a mortgage loan for others shall not be exempt from the provisions of this chapter;</p></li><li><p>Persons acting in a fiduciary capacity conferred by authority of any court;</p></li><li><p>Persons licensed as small business investment companies by the Small Business Administration;</p></li><li><p>The Virginia Housing Development Authority and persons who (i) are approved by the Virginia Housing Development Authority pursuant to its rules and regulations to act as field originators with respect to mortgage loans made under its programs and (ii) are not engaged in any other activities for which a license is required to be obtained under this chapter; and</p></li><li><p>Persons who make loans or extend credit for any part of the purchase price of real property owned by such person.</p></li></ol><p>1987, c. 596, § 6.1-411; 1989, cc. 411, 667; 1992, c. 156; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0062" target="_blank" rel="noreferrer">62</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0126" target="_blank" rel="noreferrer">126</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0386" target="_blank" rel="noreferrer">386</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>.</p>',4),n=[o];function s(l,p,c,g,h,f){return r(),a("div",null,n)}const u=e(i,[["render",s]]);export{m as __pageData,u as default};
