import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1603 Application for license; form; content; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1603.md","filePath":"vacode/6.2-1603.md"}'),t={name:"vacode/6.2-1603.md"},n=o('<h1 id="§-6-2-1603-application-for-license-form-content-fee" tabindex="-1">§ 6.2-1603 Application for license; form; content; fee. <a class="header-anchor" href="#§-6-2-1603-application-for-license-form-content-fee" aria-label="Permalink to &quot;§ 6.2-1603 Application for license; form; content; fee.&quot;">​</a></h1><p>A. An application for a license under this chapter shall be made in writing, under oath and on a form provided by the Commissioner.</p><p>B. The application shall set forth:</p><ol><li><p>The name and address of the applicant;</p></li><li><p>If the applicant is a firm or partnership, the name and address of each member of the firm or partnership;</p></li><li><p>If the applicant is a corporation or a limited liability company, the name and address of each officer, director, registered agent and each principal;</p></li><li><p>The address of each office at which the business to be licensed is to be conducted;</p></li><li><p>Whether the applicant seeks a license to act as a mortgage lender, mortgage broker, or both; and</p></li><li><p>Such other information concerning the financial responsibility, background, experience and activities of the applicant and its members, officers, directors, and principals as the Commissioner may require.</p></li></ol><p>C. The application shall be accompanied by payment of the following fees:</p><ol><li>A license application fee of:</li></ol><p>a. In the case of an application for a license to act as a mortgage lender or a mortgage broker, $500; or</p><p>b. In the case of an application for a license to act as both mortgage lender and mortgage broker, $1,000; and</p><ol start="2"><li>An application fee of $150 for each office at which the business to be licensed is to be conducted.</li></ol><p>D. The application fees shall not be refundable in any event. The fees shall not be abated by surrender, suspension or revocation of the license.</p><p>1987, c. 596, § 6.1-412; 1989, c. 411; 1992, c. 283; 1993, c. 183; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0435" target="_blank" rel="noreferrer">435</a>.</p>',11),r=[n];function c(l,p,s,f,h,d){return a(),i("div",null,r)}const _=e(t,[["render",c]]);export{b as __pageData,_ as default};
