import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1614 Prohibitions applicable to mortgage lenders and mortgage brokers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1614.md","filePath":"vacode/6.2-1614.md"}'),n={name:"vacode/6.2-1614.md"},i=t('<h1 id="§-6-2-1614-prohibitions-applicable-to-mortgage-lenders-and-mortgage-brokers" tabindex="-1">§ 6.2-1614 Prohibitions applicable to mortgage lenders and mortgage brokers. <a class="header-anchor" href="#§-6-2-1614-prohibitions-applicable-to-mortgage-lenders-and-mortgage-brokers" aria-label="Permalink to &quot;§ 6.2-1614 Prohibitions applicable to mortgage lenders and mortgage brokers.&quot;">​</a></h1><p>No mortgage lender or mortgage broker required to be licensed under this chapter shall:</p><ol><li><p>Obtain any agreement or instrument in which blanks are left to be filled in after execution;</p></li><li><p>Take an interest in collateral other than the real estate or residential property securing a mortgage loan, including any fixtures and appliances thereon and any mobile or manufactured home placed on such real estate even if such mobile or manufactured home is not permanently affixed thereto;</p></li><li><p>Obtain any exclusive dealing or exclusive agency agreement from any borrower;</p></li><li><p>Delay closing of any mortgage loan for the purpose of increasing interest, costs, fees, or charges payable by the borrower;</p></li><li><p>Obtain any agreement or instrument executed by a borrower which contains an acceleration clause permitting the unpaid balance of a mortgage loan to be declared due for any reason other than failing to make timely payments of interest and principal, submitting false information in connection with an application for the mortgage loan, breaching any representation or covenant made in the agreement or instrument, or failing to perform any other obligations undertaken in the agreement or instrument;</p></li><li><p>Recommend or encourage a person to default on an existing loan or other debt, if such default adversely affects such person&#39;s creditworthiness, in connection with the solicitation or making of a mortgage loan that refinances all or any portion of such existing loan or debt;</p></li><li><p>Knowingly or intentionally engage in the act or practice of refinancing a mortgage loan within 12 months following the date the refinanced mortgage loan was originated, unless the refinancing is in the borrower&#39;s best interest, which act or practice is commonly referred to as &quot;flipping.&quot; This prohibition shall apply regardless of whether the interest rate, points, fees, and charges paid or payable by the borrower in connection with the refinancing exceed any limitation established pursuant to Chapter 3 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-300/" target="_blank" rel="noreferrer">6.2-300</a> et seq.) or Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-406/" target="_blank" rel="noreferrer">6.2-406</a> et seq.) of Chapter 4. Factors to be considered in determining if the refinancing is in the borrower&#39;s best interest include but are not limited to whether:</p></li></ol><p>a. The borrower&#39;s new monthly payment is lower than the total of all monthly obligations being financed, taking into account the costs and fees;</p><p>b. There is a change in the amortization period of the new loan;</p><p>c. The borrower receives cash in excess of the costs and fees of refinancing;</p><p>d. The rate of interest on the borrower&#39;s note is reduced;</p><p>e. There is a change from an adjustable to a fixed rate loan, taking into account costs and fees; and</p><p>f. The refinancing is necessary to respond to a bona fide personal need or an order of an appropriate court; or</p><ol start="8"><li>Use or cause to be published any advertisement that:</li></ol><p>a. Contains any false, misleading, or deceptive statement or representation; or</p><p>b. Identifies the mortgage lender or mortgage broker by any name other than the name set forth on the license issued by the Commission.</p><p>1987, c. 596, §§ 6.1-422, 6.1-424; 1989, c. 667; 1993, c. 183; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0062" target="_blank" rel="noreferrer">62</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0228" target="_blank" rel="noreferrer">228</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0510" target="_blank" rel="noreferrer">510</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>, § 6.1-422.1; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0386" target="_blank" rel="noreferrer">386</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0189" target="_blank" rel="noreferrer">189</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0261" target="_blank" rel="noreferrer">261</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',13),o=[i];function l(s,c,g,h,p,f){return r(),a("div",null,o)}const m=e(n,[["render",l]]);export{b as __pageData,m as default};
