import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1619 Suspension or revocation of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1619.md","filePath":"vacode/6.2-1619.md"}'),t={name:"vacode/6.2-1619.md"},a=i('<h1 id="§-6-2-1619-suspension-or-revocation-of-license" tabindex="-1">§ 6.2-1619 Suspension or revocation of license. <a class="header-anchor" href="#§-6-2-1619-suspension-or-revocation-of-license" aria-label="Permalink to &quot;§ 6.2-1619 Suspension or revocation of license.&quot;">​</a></h1><p>A. The Commission may suspend or revoke any license issued under this chapter to a mortgage lender or mortgage broker upon any of the following grounds:</p><ol><li><p>Any ground for denial of a license under this chapter;</p></li><li><p>Any violation of the provisions of this chapter or regulations adopted by the Commission pursuant thereto, or a violation of any other law or regulation applicable to the conduct of the mortgage lender&#39;s or mortgage broker&#39;s business;</p></li><li><p>A course of conduct consisting of the failure to perform written agreements with borrowers;</p></li><li><p>Failure to account for funds received or disbursed to the satisfaction of the person supplying or receiving such funds;</p></li><li><p>Failure to pay when due reasonable fees to a licensed appraiser for appraisal services that are (i) requested from the appraiser in writing by the mortgage broker or mortgage lender or an employee of the mortgage broker or mortgage lender and (ii) performed, in accordance with the terms of the contract with the appraiser and all regulatory requirements related to such appraiser and appraisal, by the appraiser in connection with the origination or closing of a mortgage loan for a customer of the mortgage broker or mortgage lender;</p></li><li><p>Failure to disburse funds in accordance with any agreement connected with, and promptly upon closing of, a mortgage loan, taking into account any applicable right of rescission;</p></li><li><p>Conviction of a felony or misdemeanor involving fraud, misrepresentation or deceit;</p></li><li><p>Entry of a judgment against the licensee involving fraud, misrepresentation or deceit;</p></li><li><p>Entry of a federal or state administrative order against the licensee for violation of any law or any regulation applicable to the conduct of his business;</p></li><li><p>Refusal to permit an investigation or examination by the Commission;</p></li><li><p>Failure to pay any fee or assessment imposed by this chapter; or</p></li><li><p>Failure to comply with any order of the Commission.</p></li></ol><p>B. For the purposes of this section, acts of any officer, director, member, partner, or principal shall be deemed acts of the licensee.</p><p>1987, c. 596, § 6.1-425; 1993, c. 183; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),n=[a];function s(l,p,c,d,f,h){return o(),r("div",null,n)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
