import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-1624 Civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1624.md","filePath":"vacode/6.2-1624.md"}'),i={name:"vacode/6.2-1624.md"},o=r('<h1 id="§-6-2-1624-civil-penalties" tabindex="-1">§ 6.2-1624 Civil penalties. <a class="header-anchor" href="#§-6-2-1624-civil-penalties" aria-label="Permalink to &quot;§ 6.2-1624 Civil penalties.&quot;">​</a></h1><p>In addition to the authority conferred under §§ <a href="http://law.lis.virginia.gov/vacode/6.2-1619/" target="_blank" rel="noreferrer">6.2-1619</a> and <a href="http://law.lis.virginia.gov/vacode/6.2-1622/" target="_blank" rel="noreferrer">6.2-1622</a>, the Commission may impose a civil penalty not exceeding $2,500 upon any mortgage lender or mortgage broker required to be licensed under this chapter who it determines, in proceedings commenced in accordance with the Commission&#39;s Rules, has violated any of the provisions of this chapter or any other law or regulation applicable to the conduct of the mortgage lender&#39;s or mortgage broker&#39;s business. For the purposes of this section, each separate violation shall be subject to the civil penalty herein prescribed, and each day after the date of notification, excluding Sundays and holidays, as prescribed in § <a href="http://law.lis.virginia.gov/vacode/2.2-3300/" target="_blank" rel="noreferrer">2.2-3300</a>, that an unlicensed person engages in the business or holds himself out to the general public as a mortgage lender or mortgage broker shall constitute a separate violation.</p><p>1987, c. 596, § 6.1-428; 1990, c. 4; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),n=[o];function s(l,c,h,d,p,g){return a(),t("div",null,n)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
