import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 6.2-1626 Authority of Attorney General; referral by Commission to Attorney General.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1626.md","filePath":"vacode/6.2-1626.md"}'),o={name:"vacode/6.2-1626.md"},n=a('<h1 id="§-6-2-1626-authority-of-attorney-general-referral-by-commission-to-attorney-general" tabindex="-1">§ 6.2-1626 Authority of Attorney General; referral by Commission to Attorney General. <a class="header-anchor" href="#§-6-2-1626-authority-of-attorney-general-referral-by-commission-to-attorney-general" aria-label="Permalink to &quot;§ 6.2-1626 Authority of Attorney General; referral by Commission to Attorney General.&quot;">​</a></h1><p>A. If the Commission determines that a licensee is in violation of, or has violated, § <a href="http://law.lis.virginia.gov/vacode/6.2-1614/" target="_blank" rel="noreferrer">6.2-1614</a>, <a href="http://law.lis.virginia.gov/vacode/6.2-1615/" target="_blank" rel="noreferrer">6.2-1615</a>, or <a href="http://law.lis.virginia.gov/vacode/6.2-1616/" target="_blank" rel="noreferrer">6.2-1616</a>, or any provision of Chapter 3 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-300/" target="_blank" rel="noreferrer">6.2-300</a> et seq.) or Chapter 4 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-400/" target="_blank" rel="noreferrer">6.2-400</a> et seq.), the Commission may refer the information to the Attorney General and may request that the Attorney General investigate such violations.</p><p>B. Upon such referral, the Attorney General:</p><ol><li><p>May seek to enjoin violations of such laws. The appropriate circuit court may enjoin such violations notwithstanding the existence of an adequate remedy at law; and</p></li><li><p>May seek, and the circuit court may order or decree, damages and such other relief allowed by law, including restitution to the extent available to borrowers under applicable law.</p></li></ol><p>C. Persons entitled to any relief authorized by this section shall be identified by order of the court within 180 days from the date of the order permanently enjoining the unlawful act or practice.</p><p>D. In any action brought by the Attorney General by virtue of the authority granted in this section, the Attorney General shall be entitled to seek attorney fees and costs.</p><p>E. The Attorney General shall be authorized to bring an action to enjoin violations of the Real Estate Settlement Procedures Act of 1974 (RESPA) (12 U.S.C. § 2601 et seq.), to the extent authorized by §§ 8 and 16 of RESPA (12 U.S.C. §§ 2607 and 2614).</p><p>1987, c. 596, § 6.1-430; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0510" target="_blank" rel="noreferrer">510</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0863" target="_blank" rel="noreferrer">863</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0204" target="_blank" rel="noreferrer">204</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',8),i=[n];function l(s,h,c,f,d,g){return t(),r("div",null,i)}const u=e(o,[["render",l]]);export{y as __pageData,u as default};
