import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1702 Application for license; form; content; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1702.md","filePath":"vacode/6.2-1702.md"}'),n={name:"vacode/6.2-1702.md"},o=t('<h1 id="§-6-2-1702-application-for-license-form-content-fee" tabindex="-1">§ 6.2-1702 Application for license; form; content; fee. <a class="header-anchor" href="#§-6-2-1702-application-for-license-form-content-fee" aria-label="Permalink to &quot;§ 6.2-1702 Application for license; form; content; fee.&quot;">​</a></h1><p>A. An application for a license under this chapter shall be on a form provided by the Registry.</p><p>B. The application shall set forth:</p><ol><li><p>The name and residential address of the applicant;</p></li><li><p>The address of the applicant&#39;s employer or the address where the applicant will act as a mortgage loan originator, as applicable; and</p></li><li><p>Such other information concerning the financial responsibility, background, experience, and activities of the applicant as the Commissioner may require.</p></li></ol><p>C. The application shall be accompanied by payment of an application fee in an amount not to exceed $150, or a lesser amount as may be prescribed by the Commission. The application fee shall be in addition to any other fees payable by the applicant, including but not limited to fees for pre-licensing education and testing, fingerprinting, criminal background checks, credit reports, or administrative fees charged by the Registry.</p><p>D. The application fee shall not be refundable in any event. The fee shall not be abated by surrender, suspension, or revocation of the license.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, § 6.1-431.3; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',7),r=[o];function l(c,p,s,f,h,d){return a(),i("div",null,r)}const g=e(n,[["render",l]]);export{_ as __pageData,g as default};
