import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1718 Notice of proposed suspension or revocation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1718.md","filePath":"vacode/6.2-1718.md"}'),n={name:"vacode/6.2-1718.md"},s=i('<h1 id="§-6-2-1718-notice-of-proposed-suspension-or-revocation" tabindex="-1">§ 6.2-1718 Notice of proposed suspension or revocation. <a class="header-anchor" href="#§-6-2-1718-notice-of-proposed-suspension-or-revocation" aria-label="Permalink to &quot;§ 6.2-1718 Notice of proposed suspension or revocation.&quot;">​</a></h1><p>The Commission may not revoke or suspend the license of any licensee under this chapter unless it has given the licensee 21 days&#39; notice in writing of the reasons for the proposed revocation or suspension and has given the licensee an opportunity to introduce evidence and be heard. The notice shall be sent by certified mail to the licensee&#39;s last address on the Commission&#39;s records and shall state with particularity the basis for the contemplated action. Within 14 days of mailing the notice, the licensee may file with the clerk of the Commission a written request for a hearing. If a hearing is requested, the Commission shall not suspend or revoke the license except based upon findings made at such hearing. The hearing shall be conducted in accordance with the provisions of Title 12.1.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, § 6.1-431.19; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),a=[s];function r(c,h,l,p,d,f){return o(),t("div",null,a)}const g=e(n,[["render",r]]);export{u as __pageData,g as default};
