import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-1719 Civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1719.md","filePath":"vacode/6.2-1719.md"}'),r={name:"vacode/6.2-1719.md"},n=t('<h1 id="§-6-2-1719-civil-penalties" tabindex="-1">§ 6.2-1719 Civil penalties. <a class="header-anchor" href="#§-6-2-1719-civil-penalties" aria-label="Permalink to &quot;§ 6.2-1719 Civil penalties.&quot;">​</a></h1><p>The Commission may impose a civil penalty not exceeding $2,500 upon any individual required to be licensed under this chapter who it determines, in proceedings commenced in accordance with the Commission&#39;s Rules, has violated any of the provisions of this chapter or any other law or regulation applicable to the licensee&#39;s activities. For the purposes of this section, each separate violation shall be subject to the civil penalty herein prescribed, and each day that an unlicensed individual engages in the business of a mortgage loan originator shall constitute a separate violation.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, § 6.1-431.20; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0187" target="_blank" rel="noreferrer">187</a>.</p>',3),o=[n];function l(s,c,p,h,d,g){return i(),a("div",null,o)}const f=e(r,[["render",l]]);export{v as __pageData,f as default};
