import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1807 Licenses; places of offices; changes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1807.md","filePath":"vacode/6.2-1807.md"}'),n={name:"vacode/6.2-1807.md"},t=o('<h1 id="§-6-2-1807-licenses-places-of-offices-changes" tabindex="-1">§ 6.2-1807 Licenses; places of offices; changes. <a class="header-anchor" href="#§-6-2-1807-licenses-places-of-offices-changes" aria-label="Permalink to &quot;§ 6.2-1807 Licenses; places of offices; changes.&quot;">​</a></h1><p>A. Each license shall:</p><ol><li><p>State the address of each approved office at which the business is to be conducted;</p></li><li><p>State fully the name of the licensee; and</p></li><li><p>Be prominently posted in each office of the licensee.</p></li></ol><p>B. No licensee shall:</p><ol><li><p>Use any name other than the name set forth on the license issued by the Commission; or</p></li><li><p>Open an additional office or relocate any office without prior approval of the Commission.</p></li></ol><p>C. Applications for Commission approval to open an additional office or relocate any office shall be made in writing on a form provided by the Commissioner and shall be accompanied by payment of a $150 nonrefundable application fee or other reasonable amount as the Commission may prescribe by regulation. The application shall be approved unless the Commission finds that the applicant does not have the required liquid assets or has not conducted business under this chapter efficiently, fairly, in the public interest, and in accordance with law. The application shall be deemed approved if notice to the contrary has not been mailed by the Commission to the applicant within 30 days of the date the application is received by the Commission. After approval, the applicant shall give written notice to the Commissioner within 10 days of the commencement of business at the additional office or relocated office.</p><p>D. Every licensee shall within 10 days notify the Commissioner, in writing, of the closing of any office and of the name, address, and position of each new senior officer, member, partner, or director and provide such other information with respect to any such change as the Commissioner may reasonably require.</p><p>E. Licenses shall:</p><ol><li><p>Not be transferable or assignable, by operation of law or otherwise; and</p></li><li><p>Remain in force until they have been surrendered, revoked, or suspended. The surrender, revocation, or suspension of a license shall not affect any preexisting legal right or obligation of the licensee.</p></li></ol><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>, § 6.1-451; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',10),s=[t];function r(l,c,h,p,f,d){return i(),a("div",null,s)}const g=e(n,[["render",r]]);export{b as __pageData,g as default};
