import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-1809 Retention of books, accounts, and records.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1809.md","filePath":"vacode/6.2-1809.md"}'),t={name:"vacode/6.2-1809.md"},n=o('<h1 id="§-6-2-1809-retention-of-books-accounts-and-records" tabindex="-1">§ 6.2-1809 Retention of books, accounts, and records. <a class="header-anchor" href="#§-6-2-1809-retention-of-books-accounts-and-records" aria-label="Permalink to &quot;§ 6.2-1809 Retention of books, accounts, and records.&quot;">​</a></h1><p>Every licensee shall maintain in its approved offices such books, accounts and records as the Commission may reasonably require in order to determine whether such licensee is complying with the provisions of this chapter and regulations adopted in furtherance thereof. Such books, accounts and records shall be maintained apart and separate from any other business in which the licensee is involved. Such records relating to short-term loans, including copies of checks given to a licensee as security for such loans, shall be retained for at least three years after final payment is made on any loan.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>, § 6.1-453; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0295" target="_blank" rel="noreferrer">295</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',3),i=[n];function s(c,l,d,h,f,p){return a(),r("div",null,i)}const u=e(t,[["render",s]]);export{g as __pageData,u as default};
