import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-1812 Other reporting requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1812.md","filePath":"vacode/6.2-1812.md"}'),n={name:"vacode/6.2-1812.md"},o=t('<h1 id="§-6-2-1812-other-reporting-requirements" tabindex="-1">§ 6.2-1812 Other reporting requirements. <a class="header-anchor" href="#§-6-2-1812-other-reporting-requirements" aria-label="Permalink to &quot;§ 6.2-1812 Other reporting requirements.&quot;">​</a></h1><p>A. A licensee shall file a written report with the Commissioner within 15 days following the occurrence of any of the following:</p><ol><li><p>The filing of bankruptcy, reorganization or receivership proceedings by or against the licensee;</p></li><li><p>The institution of administrative or regulatory proceedings against the licensee by any governmental authority;</p></li><li><p>Any felony indictments of the licensee or any of its members, partners, directors, officers, or principals;</p></li><li><p>Any felony conviction of the licensee or any of its members, partners, directors, officers, or principals; and</p></li><li><p>Such other event as the Commission may prescribe by regulation.</p></li></ol><p>B. The report shall be in writing and describe the event and its expected impact on the business of the licensee.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>, § 6.1-455; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),a=[o];function s(l,c,p,h,f,d){return r(),i("div",null,a)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
