import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1818.3 Restriction on certain fees and charges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1818.3.md","filePath":"vacode/6.2-1818.3.md"}'),r={name:"vacode/6.2-1818.3.md"},o=n('<h1 id="§-6-2-1818-3-restriction-on-certain-fees-and-charges" tabindex="-1">§ 6.2-1818.3 Restriction on certain fees and charges. <a class="header-anchor" href="#§-6-2-1818-3-restriction-on-certain-fees-and-charges" aria-label="Permalink to &quot;§ 6.2-1818.3 Restriction on certain fees and charges.&quot;">​</a></h1><p>Notwithstanding any provision of this chapter to the contrary, a licensee shall not contract for, charge, collect, or receive in connection with a short-term loan a total amount of fees and charges that exceeds either (i) 50 percent of the originally contracted loan amount, if the originally contracted loan amount was $1,500 or less or (ii) 60 percent of the originally contracted loan amount, if the originally contracted loan amount was greater than $1,500. For purposes of this section, all charges made in connection with the loan shall be included when calculating the total loan charges except for deposit item return fees and late charges authorized under § <a href="/vacode/6.2-1817/">6.2-1817</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',3),i=[o];function c(s,l,h,d,f,_){return t(),a("div",null,i)}const u=e(r,[["render",c]]);export{p as __pageData,u as default};
