import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-1826 Validity of noncompliant loan agreement; private right of action.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1826.md","filePath":"vacode/6.2-1826.md"}'),i={name:"vacode/6.2-1826.md"},n=o('<h1 id="§-6-2-1826-validity-of-noncompliant-loan-agreement-private-right-of-action" tabindex="-1">§ 6.2-1826 Validity of noncompliant loan agreement; private right of action. <a class="header-anchor" href="#§-6-2-1826-validity-of-noncompliant-loan-agreement-private-right-of-action" aria-label="Permalink to &quot;§ 6.2-1826 Validity of noncompliant loan agreement; private right of action.&quot;">​</a></h1><p>A. If any provision of a written loan agreement violates this chapter, such provision shall be unenforceable against the borrower.</p><p>B. Any person who suffers loss by reason of a violation of any provision of this chapter may bring a civil action to enforce such provision. Any person who is successful in such action shall recover reasonable attorney fees, expert witness fees, and court costs incurred by bringing such action.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>, § 6.1-469; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0849" target="_blank" rel="noreferrer">849</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0876" target="_blank" rel="noreferrer">876</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[n];function c(l,s,p,f,h,g){return a(),t("div",null,r)}const d=e(i,[["render",c]]);export{v as __pageData,d as default};
