import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-1827 Application of chapter to Internet loans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1827.md","filePath":"vacode/6.2-1827.md"}'),i={name:"vacode/6.2-1827.md"},n=r('<h1 id="§-6-2-1827-application-of-chapter-to-internet-loans" tabindex="-1">§ 6.2-1827 Application of chapter to Internet loans. <a class="header-anchor" href="#§-6-2-1827-application-of-chapter-to-internet-loans" aria-label="Permalink to &quot;§ 6.2-1827 Application of chapter to Internet loans.&quot;">​</a></h1><p>A. The provisions of this chapter, including specifically the licensure requirements of § <a href="/vacode/6.2-1801/">6.2-1801</a>, shall apply to persons making short-term loans over the Internet to Virginia residents or any individual in the Commonwealth, whether or not the person making the loan maintains a physical presence in the Commonwealth.</p><p>B. The Commission may, from time to time, by administrative rule or policy statement, set requirements that the Commission reasonably deems necessary to ensure compliance with this section.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0849" target="_blank" rel="noreferrer">849</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0876" target="_blank" rel="noreferrer">876</a>, § 6.1-469.1; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',4),o=[n];function l(s,c,p,h,f,_){return t(),a("div",null,o)}const d=e(i,[["render",l]]);export{g as __pageData,d as default};
