import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1901 License required; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1901.md","filePath":"vacode/6.2-1901.md"}'),a={name:"vacode/6.2-1901.md"},n=i('<h1 id="§-6-2-1901-license-required-exception" tabindex="-1">§ 6.2-1901 License required; exception. <a class="header-anchor" href="#§-6-2-1901-license-required-exception" aria-label="Permalink to &quot;§ 6.2-1901 License required; exception.&quot;">​</a></h1><p>A. No person shall engage in the business of selling money orders or engage in the business of money transmission, whether or not the person has a location in the Commonwealth, unless the person obtains from the Commission a license issued pursuant to this chapter.</p><p>B. No license under this chapter shall be required of any authorized delegate of a licensee.</p><p>C. Every person required to be licensed under this chapter shall register with the Registry and be subject to such registration and renewal requirements as may be established by the Registry, in addition to any requirements of this chapter. In adopting regulations pursuant to § <a href="/vacode/6.2-1913/">6.2-1913</a>, the Commission shall include any terms, conditions, or requirements applicable to such registration and renewal. Any fees required by the Registry shall be separate and apart from any fees imposed by this chapter. The Commission, at its discretion, may collect any registration and renewal fees on behalf of the Registry and remit such fees to the Registry or permit the Registry to collect any fees imposed by this chapter and remit such fees to the Commission.</p><p>D. In connection with its implementation and administration of this chapter, the Commission may establish agreements or contracts with the Registry or other entities designated by the Registry to collect, distribute, and maintain information and records and process fees related to persons required to be licensed under this chapter. In establishing such agreements or contracts, the Commission shall not be subject to the Virginia Public Procurement Act (§ <a href="/vacode/2.2-4300/">2.2-4300</a> et seq.).</p><p>1974, c. 578, §§ 6.1-371, 6.1-377; 1983, c. 156; 1987, c. 283; 1990, c. 259; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0634" target="_blank" rel="noreferrer">634</a>.</p>',6),s=[n];function o(c,l,h,d,p,g){return t(),r("div",null,s)}const f=e(a,[["render",o]]);export{u as __pageData,f as default};
