import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1902 Scope and construction of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1902.md","filePath":"vacode/6.2-1902.md"}'),n={name:"vacode/6.2-1902.md"},i=o('<h1 id="§-6-2-1902-scope-and-construction-of-chapter" tabindex="-1">§ 6.2-1902 Scope and construction of chapter. <a class="header-anchor" href="#§-6-2-1902-scope-and-construction-of-chapter" aria-label="Permalink to &quot;§ 6.2-1902 Scope and construction of chapter.&quot;">​</a></h1><p>A. The provisions of this chapter shall not apply to:</p><ol><li><p>The United States, or any department, instrumentality or agency thereof;</p></li><li><p>Any state, or any department, instrumentality, agency, locality, municipality, or political subdivision thereof;</p></li><li><p>Any bank, trust company, savings institution, or credit union operating under the laws of the United States or any state or territory thereof, or other person to the extent the person provides money transmission services as an agent of one or more banks, trust companies, savings institutions, or credit unions operating under the laws of the United States or any state or territory thereof;</p></li><li><p>Any private security services business, licensed under § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a>, that transports or offers to transport money; or</p></li><li><p>Any entity that has been explicitly designated in a written agreement as an agent of any governmental authority or unit identified in subdivision 1 or 2, provided that any funds collected by the agent shall be deemed for all purposes to be received by the governmental authority or unit. This subdivision shall not be construed to prohibit the governmental authority or unit from seeking indemnification from its agent for any direct losses incurred due to the agent&#39;s failure to remit funds in accordance with its agreement.</p></li></ol><p>B. This chapter shall be construed by the Commission for the purpose of protecting, against financial loss, residents of the Commonwealth who (i) purchase money orders or (ii) give money or control of their funds or credit into the custody of another person for transmission, regardless of whether the money order seller or money transmitter has any office, facility, authorized delegate, or other physical presence in the Commonwealth.</p><p>1974, c. 578, § 6.1-371; 1983, c. 156; 1987, c. 283; 1990, c. 259; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0237" target="_blank" rel="noreferrer">237</a>.</p>',5),a=[i];function s(l,c,h,p,d,f){return t(),r("div",null,a)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
