import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1906 Conditions prerequisite to issuance of license; net worth requirement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1906.md","filePath":"vacode/6.2-1906.md"}'),r={name:"vacode/6.2-1906.md"},n=a('<h1 id="§-6-2-1906-conditions-prerequisite-to-issuance-of-license-net-worth-requirement" tabindex="-1">§ 6.2-1906 Conditions prerequisite to issuance of license; net worth requirement. <a class="header-anchor" href="#§-6-2-1906-conditions-prerequisite-to-issuance-of-license-net-worth-requirement" aria-label="Permalink to &quot;§ 6.2-1906 Conditions prerequisite to issuance of license; net worth requirement.&quot;">​</a></h1><p>A. The Commission shall not issue a license to an applicant unless it determines that:</p><ol><li><p>The applicant will be able to and will perform its obligations to purchasers of money transmission services and purchasers, payees, and holders of money orders sold by it and its authorized delegates; and</p></li><li><p>The financial responsibility, character, reputation, experience, and general fitness of the applicant and its members, senior officers, directors, and principals are such as to warrant belief that the business will be operated efficiently and fairly, in the public interest, and in accordance with applicable law and regulations.</p></li></ol><p>B. Each licensee shall at all times have a net worth of not less than $200,000, or a higher amount not to exceed $1 million as determined by the Commission, calculated in accordance with generally accepted accounting principles.</p><p>1974, c. 578, § 6.1-374; 1990, c. 259; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0454" target="_blank" rel="noreferrer">454</a>.</p>',5),o=[n];function s(l,c,p,h,d,f){return i(),t("div",null,o)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
