import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1911 Conduct of business through authorized delegates of licensee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1911.md","filePath":"vacode/6.2-1911.md"}'),r={name:"vacode/6.2-1911.md"},o=i('<h1 id="§-6-2-1911-conduct-of-business-through-authorized-delegates-of-licensee" tabindex="-1">§ 6.2-1911 Conduct of business through authorized delegates of licensee. <a class="header-anchor" href="#§-6-2-1911-conduct-of-business-through-authorized-delegates-of-licensee" aria-label="Permalink to &quot;§ 6.2-1911 Conduct of business through authorized delegates of licensee.&quot;">​</a></h1><p>A. A licensee may conduct its business through or by means of such authorized delegates as the licensee may designate or appoint under a written agreement with such authorized delegates. The agreement between a licensee and an authorized delegate shall (i) require the authorized delegate to comply with the provisions of this chapter and all other applicable state and federal laws and regulations; (ii) require the authorized delegate to remit all sums owing to the licensee in accordance with the terms of the written agreement; (iii) require the authorized delegate to permit the Commission to investigate or examine its business pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-1910/" target="_blank" rel="noreferrer">6.2-1910</a>; and (iv) prohibit the authorized delegate from using a subdelegate, or from otherwise designating or appointing another person to sell money orders or engage in money transmission business on behalf of the licensee.</p><p>B. A licensee shall conduct a due diligence review of all new authorized delegates. A licensee shall be responsible for implementing and maintaining a reasonable risk-based supervision program to monitor its authorized delegates.</p><p>1974, c. 578, § 6.1-377; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[o];function s(l,h,d,c,g,u){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{p as __pageData,m as default};
