import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-1915 Sale or issuance of bearer money orders; prohibition.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1915.md","filePath":"vacode/6.2-1915.md"}'),a={name:"vacode/6.2-1915.md"},t=i('<h1 id="§-6-2-1915-sale-or-issuance-of-bearer-money-orders-prohibition" tabindex="-1">§ 6.2-1915 Sale or issuance of bearer money orders; prohibition. <a class="header-anchor" href="#§-6-2-1915-sale-or-issuance-of-bearer-money-orders-prohibition" aria-label="Permalink to &quot;§ 6.2-1915 Sale or issuance of bearer money orders; prohibition.&quot;">​</a></h1><p>A. No authorized delegate of a licensee shall sell a money order with a face amount of $750 or more that does not designate a specific payee.</p><p>B. This section applies only to paper money orders.</p><p>C. This section does not apply to (i) travelers checks, (ii) electronic instruments, (iii) stored value products or other similar instruments for the transmission or payment of money, or (iv) money orders sold or issued by insured financial institutions.</p><p>D. Licensees shall inform their authorized delegates of the obligations imposed by this section.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0355" target="_blank" rel="noreferrer">355</a>, § 6.1-378.3; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),s=[t];function n(l,c,p,d,h,f){return o(),r("div",null,s)}const u=e(a,[["render",n]]);export{m as __pageData,u as default};
