import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-1920 Civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1920.md","filePath":"vacode/6.2-1920.md"}'),i={name:"vacode/6.2-1920.md"},o=r('<h1 id="§-6-2-1920-civil-penalties" tabindex="-1">§ 6.2-1920 Civil penalties. <a class="header-anchor" href="#§-6-2-1920-civil-penalties" aria-label="Permalink to &quot;§ 6.2-1920 Civil penalties.&quot;">​</a></h1><p>In addition to the authority conferred under §§ <a href="http://law.lis.virginia.gov/vacode/6.2-1907/" target="_blank" rel="noreferrer">6.2-1907</a> and <a href="http://law.lis.virginia.gov/vacode/6.2-1909/" target="_blank" rel="noreferrer">6.2-1909</a>, the Commission may impose a civil penalty not exceeding $2,500 upon any person licensed or required to be licensed under this chapter who the Commission determines has violated any of the provisions of this chapter or any other law or regulation applicable to the conduct of the person&#39;s business. For the purposes of this section, each separate violation shall be subject to the civil penalty herein prescribed. In the case of a violation of § <a href="http://law.lis.virginia.gov/vacode/6.2-1901/" target="_blank" rel="noreferrer">6.2-1901</a>, each money order sale or money transmission transaction shall constitute a separate violation.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>, § 6.1-378.4; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),n=[o];function s(l,c,h,p,d,_){return a(),t("div",null,n)}const g=e(i,[["render",s]]);export{f as __pageData,g as default};
