import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-2002 Application for license; form; content; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2002.md","filePath":"vacode/6.2-2002.md"}'),t={name:"vacode/6.2-2002.md"},r=n('<h1 id="§-6-2-2002-application-for-license-form-content-fee" tabindex="-1">§ 6.2-2002 Application for license; form; content; fee. <a class="header-anchor" href="#§-6-2-2002-application-for-license-form-content-fee" aria-label="Permalink to &quot;§ 6.2-2002 Application for license; form; content; fee.&quot;">​</a></h1><p>A. An application for a license under this chapter shall be made in writing, under oath, and on a form provided by the Commissioner.</p><p>B. The application shall include:</p><ol><li><p>The name and address of the applicant; and (i) if the applicant is a partnership, firm, or association, the name and address of each partner or member; (ii) if the applicant is a corporation or limited liability company, the name and address of each director, member, registered agent, and principal; or (iii) if the applicant is a business trust, the name and address of each trustee and beneficiary;</p></li><li><p>The name and address of each manager and officer;</p></li><li><p>The addresses of the locations of the business to be licensed;</p></li><li><p>Financial statements for the applicant as of the most recent fiscal year;</p></li><li><p>A current copy of the agency&#39;s standard DMP agreement;</p></li><li><p>Such other information concerning the financial responsibility, background, experience, and activities of the applicant and the persons referred to in this section as the Commissioner may require;</p></li><li><p>Any other pertinent information as the Commissioner may require; and</p></li><li><p>Payment of an application fee of $500.</p></li></ol><p>C. The application fee shall not be refundable in any event. The fee shall not be abated by surrender, suspension, or revocation of the license.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, § 6.1-363.4; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0315" target="_blank" rel="noreferrer">315</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),o=[r];function s(l,c,p,f,d,h){return a(),i("div",null,o)}const u=e(t,[["render",s]]);export{_ as __pageData,u as default};
