import{_ as t,o,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-2003 Bond required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2003.md","filePath":"vacode/6.2-2003.md"}'),i={name:"vacode/6.2-2003.md"},r=e("h1",{id:"§-6-2-2003-bond-required",tabindex:"-1"},[n("§ 6.2-2003 Bond required. "),e("a",{class:"header-anchor",href:"#§-6-2-2003-bond-required","aria-label":'Permalink to "§ 6.2-2003 Bond required."'},"​")],-1),s=e("p",null,"The application for a license shall be accompanied by a bond filed with the Commissioner with corporate surety authorized to execute the bond in the Commonwealth, in the principal amount as determined by the Commission. The amount of the bond shall be not less than $25,000 nor more than $350,000. The form of the bond shall be approved by the Commission. The bond shall be continuously maintained thereafter in full force, and the Commission may require the principal amount to be adjusted as it deems necessary. The bond shall be conditioned upon the licensee performing all written agreements with consumers, correctly and accurately accounting for all funds received by the licensee in the licensed business, and conducting the licensed business in conformity with this chapter and all applicable law. Any person who may be damaged by noncompliance of the licensee with any condition of the bond may proceed on the bond against the principal or surety thereon, or both, to recover damages. The aggregate liability under the bond shall not exceed the penal sum of the bond.",-1),d=e("p",null,[n("2004, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0790",target:"_blank",rel:"noreferrer"},"790"),n(", § 6.1-363.5; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),n(".")],-1),l=[r,s,d];function c(h,m,p,u,b,f){return o(),a("div",null,l)}const y=t(i,[["render",c]]);export{g as __pageData,y as default};
