import{_ as e,o,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-2018 Suspension or revocation of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2018.md","filePath":"vacode/6.2-2018.md"}'),t={name:"vacode/6.2-2018.md"},r=n('<h1 id="§-6-2-2018-suspension-or-revocation-of-license" tabindex="-1">§ 6.2-2018 Suspension or revocation of license. <a class="header-anchor" href="#§-6-2-2018-suspension-or-revocation-of-license" aria-label="Permalink to &quot;§ 6.2-2018 Suspension or revocation of license.&quot;">​</a></h1><p>A. The Commission may suspend or revoke any license issued under this chapter upon any of the following grounds:</p><ol><li><p>Any ground for denial of a license under this chapter;</p></li><li><p>Any violation of the provisions of this chapter or regulations adopted by the Commission thereunder, or a violation of any other law or regulation applicable to the conduct of the licensee&#39;s business;</p></li><li><p>A course of conduct consisting of the failure to perform written agreements with consumers;</p></li><li><p>Conviction of a felony or misdemeanor involving fraud, misrepresentation, or deceit;</p></li><li><p>Entry of a judgment against the licensee involving fraud, misrepresentation, or deceit;</p></li><li><p>Entry of a federal or state administrative order against such licensee for violation of any law or any regulation applicable to the conduct of his business;</p></li><li><p>Refusal to permit an investigation or examination by the Commission;</p></li><li><p>Failure to pay any fee or assessment imposed by this chapter;</p></li><li><p>Failure to comply with any order of the Commission; or</p></li><li><p>Insolvency of the licensee.</p></li></ol><p>B. For the purposes of this section, acts of any officer, director, member, trustee, beneficiary, partner, or principal shall be deemed acts of the licensee.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, § 6.1-363.20; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),a=[r];function s(l,c,p,f,d,h){return o(),i("div",null,a)}const _=e(t,[["render",s]]);export{m as __pageData,_ as default};
