import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-2021 Civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2021.md","filePath":"vacode/6.2-2021.md"}'),o={name:"vacode/6.2-2021.md"},r=i('<h1 id="§-6-2-2021-civil-penalties" tabindex="-1">§ 6.2-2021 Civil penalties. <a class="header-anchor" href="#§-6-2-2021-civil-penalties" aria-label="Permalink to &quot;§ 6.2-2021 Civil penalties.&quot;">​</a></h1><p>A. In addition to the authority conferred under §§ <a href="http://law.lis.virginia.gov/vacode/6.2-2018/" target="_blank" rel="noreferrer">6.2-2018</a> and <a href="http://law.lis.virginia.gov/vacode/6.2-2019/" target="_blank" rel="noreferrer">6.2-2019</a>, the Commission may impose a civil penalty not exceeding $1,000 upon any person who it determines, in proceedings commenced in accordance with the Commission&#39;s Rules, has violated any of the provisions of this chapter. For the purposes of this section, each separate violation shall be subject to the civil penalty herein prescribed. In the case of a violation of § <a href="http://law.lis.virginia.gov/vacode/6.2-2001/" target="_blank" rel="noreferrer">6.2-2001</a>, each DMP entered into shall constitute a separate violation.</p><p>B. The Commission shall have jurisdiction to impose civil penalties upon any person, regardless of whether such person is present in the Commonwealth, who obtains money or funds from a consumer for transmission to the consumer&#39;s creditors.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, § 6.1-363.23; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[r];function s(l,c,h,p,d,_){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export{f as __pageData,m as default};
