import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-2110 Revocation of registration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2110.md","filePath":"vacode/6.2-2110.md"}'),i={name:"vacode/6.2-2110.md"},a=r('<h1 id="§-6-2-2110-revocation-of-registration" tabindex="-1">§ 6.2-2110 Revocation of registration. <a class="header-anchor" href="#§-6-2-2110-revocation-of-registration" aria-label="Permalink to &quot;§ 6.2-2110 Revocation of registration.&quot;">​</a></h1><p>A. The Commission may revoke a registration under this chapter upon any of the following grounds:</p><ol><li><p>Any violation of the provisions of this chapter or regulations adopted thereunder or of any law or regulation applicable to the conduct of the registrant&#39;s business;</p></li><li><p>Charging fees for cashing items in excess of fees posted at any place of business or filed with the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-2105/" target="_blank" rel="noreferrer">6.2-2105</a>;</p></li><li><p>Conviction of a felony or misdemeanor involving fraud, misrepresentation, deceit, false swearing, or theft; or</p></li><li><p>Refusal to permit or respond to an investigation by the Commission.</p></li></ol><p>B. For the purposes of this section, acts of any officer, director, member, partner, or principal shall be deemed acts of the registrant.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>, § 6.1-442; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),n=[a];function s(l,c,p,f,h,d){return o(),t("div",null,n)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
