import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-2201 License required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2201.md","filePath":"vacode/6.2-2201.md"}'),t={name:"vacode/6.2-2201.md"},a=i('<h1 id="§-6-2-2201-license-required" tabindex="-1">§ 6.2-2201 License required. <a class="header-anchor" href="#§-6-2-2201-license-required" aria-label="Permalink to &quot;§ 6.2-2201 License required.&quot;">​</a></h1><p>A. Unless exempted from the provisions of this chapter pursuant to § <a href="/vacode/6.2-2202/">6.2-2202</a>:</p><ol><li><p>No person shall engage in the business of making motor vehicle title loans to residents of the Commonwealth or to any individuals in the Commonwealth, whether or not the person has a location in the Commonwealth, except in accordance with the provisions of this chapter and without having first obtained a license under this chapter from the Commission;</p></li><li><p>No person shall engage in the business of arranging or brokering motor vehicle title loans for residents of the Commonwealth, or any individuals in the Commonwealth, whether or not the person has a location in the Commonwealth; and</p></li><li><p>Any loan made in violation of this section is void, and no person shall have the right to collect, receive, or retain any principal, interest, fees, or other charges in connection with the loan.</p></li></ol><p>B. The provisions of subsection A shall apply to any person who seeks to evade its application by any device, subterfuge, or pretense whatsoever, including:</p><ol><li><p>The loan, forbearance, use, or sale of (i) credit, as guarantor, surety, endorser, comaker, or otherwise; (ii) money; (iii) goods; or (iv) things in action;</p></li><li><p>The use of collateral or related sales or purchases of goods or services, or agreements to sell or purchase, whether real or pretended; receiving or charging compensation for goods or services, whether or not sold, delivered, or provided; and</p></li><li><p>The real or pretended negotiation, arrangement, or procurement of a loan through any use or activity of a third person, whether real or fictitious.</p></li></ol><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, § 6.1-481; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',6),n=[a];function s(l,h,c,p,d,g){return o(),r("div",null,n)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
