import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-2203 Application for license; form; content; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2203.md","filePath":"vacode/6.2-2203.md"}'),t={name:"vacode/6.2-2203.md"},r=n('<h1 id="§-6-2-2203-application-for-license-form-content-fee" tabindex="-1">§ 6.2-2203 Application for license; form; content; fee. <a class="header-anchor" href="#§-6-2-2203-application-for-license-form-content-fee" aria-label="Permalink to &quot;§ 6.2-2203 Application for license; form; content; fee.&quot;">​</a></h1><p>A. An application for a license under this chapter shall be made in writing, under oath, and on a form provided by the Commissioner.</p><p>B. The application shall set forth:</p><ol><li><p>The name and address of the applicant and (i) if the applicant is a partnership, firm, or association, the name and address of each partner or member; (ii) if the applicant is a corporation or limited liability company, the name and address of each director, member, registered agent, and principal; or (iii) if the applicant is a business trust, the name and address of each trustee and beneficiary;</p></li><li><p>The addresses of the locations of the business to be licensed; and</p></li><li><p>Such other information concerning the financial responsibility, background, experience, and activities of the applicant and its members, officers, directors, and principals as the Commissioner may require.</p></li></ol><p>C. The application shall be accompanied by payment of an application fee of $500, or other reasonable amount that the Commission may prescribe by regulation.</p><p>D. The application fee shall not be refundable in any event. The fee shall not be abated by surrender, suspension, or revocation of the license.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, § 6.1-483; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',7),o=[r];function s(c,l,p,f,d,h){return a(),i("div",null,o)}const b=e(t,[["render",s]]);export{_ as __pageData,b as default};
