import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-2216.4 Restriction on certain fees and charges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2216.4.md","filePath":"vacode/6.2-2216.4.md"}'),n={name:"vacode/6.2-2216.4.md"},r=o('<h1 id="§-6-2-2216-4-restriction-on-certain-fees-and-charges" tabindex="-1">§ 6.2-2216.4 Restriction on certain fees and charges. <a class="header-anchor" href="#§-6-2-2216-4-restriction-on-certain-fees-and-charges" aria-label="Permalink to &quot;§ 6.2-2216.4 Restriction on certain fees and charges.&quot;">​</a></h1><p>Notwithstanding any provision of this chapter to the contrary, a licensee shall not contract for, charge, collect, or receive in connection with a motor vehicle title loan a total amount of fees and charges that exceeds either (i) 50 percent of the originally contracted loan amount, if the originally contracted loan amount was $1,500 or less, or (ii) 60 percent of the originally contracted loan amount, if the originally contracted loan amount was greater than $1,500. For purposes of this section, all charges made in connection with the loan shall be included when calculating the total loan charges except for deposit item return fees, late charges, and reasonable costs of repossession and sale authorized under § <a href="/vacode/6.2-2216/">6.2-2216</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',3),i=[r];function c(s,l,h,d,f,_){return a(),t("div",null,i)}const u=e(n,[["render",c]]);export{p as __pageData,u as default};
