import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-2224 Validity of noncompliant loan agreement; private right of action.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2224.md","filePath":"vacode/6.2-2224.md"}'),i={name:"vacode/6.2-2224.md"},n=t('<h1 id="§-6-2-2224-validity-of-noncompliant-loan-agreement-private-right-of-action" tabindex="-1">§ 6.2-2224 Validity of noncompliant loan agreement; private right of action. <a class="header-anchor" href="#§-6-2-2224-validity-of-noncompliant-loan-agreement-private-right-of-action" aria-label="Permalink to &quot;§ 6.2-2224 Validity of noncompliant loan agreement; private right of action.&quot;">​</a></h1><p>A. If any provision of a motor vehicle title loan agreement violates a requirement of this chapter, such provision shall be unenforceable against the borrower.</p><p>B. Any person who suffers loss by reason of a violation of any provision of this chapter may bring a civil action to enforce such provision. Any person who is successful in such action shall recover reasonable attorney fees, expert witness fees, and court costs incurred by bringing such action.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, § 6.1-504; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',4),r=[n];function c(s,l,p,f,h,_){return a(),o("div",null,r)}const d=e(i,[["render",c]]);export{v as __pageData,d as default};
