import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-2306 Opening box; marking contents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2306.md","filePath":"vacode/6.2-2306.md"}'),n={name:"vacode/6.2-2306.md"},r=a('<h1 id="§-6-2-2306-opening-box-marking-contents" tabindex="-1">§ 6.2-2306 Opening box; marking contents. <a class="header-anchor" href="#§-6-2-2306-opening-box-marking-contents" aria-label="Permalink to &quot;§ 6.2-2306 Opening box; marking contents.&quot;">​</a></h1><p>Upon the expiration of 60 days from the date of mailing the notice required by § <a href="http://law.lis.virginia.gov/vacode/6.2-2305/" target="_blank" rel="noreferrer">6.2-2305</a> and the failure within such period of the lessee of the box according to the records of the company to pay the amount due for the rental thereof, together with any charges for which the rental agreement provides, the company may, in the presence of two company employees, one of whom shall be a notary public, cause the box to be opened and the contents thereof, if any, to be removed, inventoried, and sealed up by the notary public in a package. The notary public shall distinctly mark upon the package the name of the lessee of the box according to the records of the company and the date of removal of the property.</p><p>Code 1950, § 6-265; 1966, c. 584, § 6.1-334; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),i=[r];function c(h,s,p,l,d,f){return t(),o("div",null,i)}const m=e(n,[["render",c]]);export{_ as __pageData,m as default};
