import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-2606 Qualifications.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2606.md","filePath":"vacode/6.2-2606.md"}'),n={name:"vacode/6.2-2606.md"},o=t('<h1 id="§-6-2-2606-qualifications" tabindex="-1">§ 6.2-2606 Qualifications. <a class="header-anchor" href="#§-6-2-2606-qualifications" aria-label="Permalink to &quot;§ 6.2-2606 Qualifications.&quot;">​</a></h1><p>A. Upon the filing and investigation of an application for a license, compliance by the applicant with the provisions of §§ <a href="/vacode/6.2-2603/">6.2-2603</a> and <a href="/vacode/6.2-2604/">6.2-2604</a>, and compliance by the persons identified in subsection B of § <a href="/vacode/6.2-2605/">6.2-2605</a> with the provisions contained therein, the Commission shall issue and deliver to the applicant the license applied for to engage in business under this chapter at the location specified in the application if it finds that:</p><ol><li><p>The financial responsibility, character, experience, and general fitness of the applicant and its members, senior officers, directors, trustees, and principals are such as to warrant belief that the business will be operated efficiently and fairly, in the public interest, and in accordance with the law;</p></li><li><p>The application does not contain any false statement of a material fact; and</p></li><li><p>The application does not omit any statement of a material fact that is required by § <a href="/vacode/6.2-2603/">6.2-2603</a>.</p></li></ol><p>B. If the Commission fails to make such findings, no license shall be issued and the Commissioner shall notify the applicant of the denial and the reasons for such denial.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1198" target="_blank" rel="noreferrer">1198</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1250" target="_blank" rel="noreferrer">1250</a>.</p>',5),s=[o];function r(l,c,p,h,f,d){return a(),i("div",null,s)}const u=e(n,[["render",r]]);export{m as __pageData,u as default};
