import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-2615 Suspension or revocation of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2615.md","filePath":"vacode/6.2-2615.md"}'),n={name:"vacode/6.2-2615.md"},r=t('<h1 id="§-6-2-2615-suspension-or-revocation-of-license" tabindex="-1">§ 6.2-2615 Suspension or revocation of license. <a class="header-anchor" href="#§-6-2-2615-suspension-or-revocation-of-license" aria-label="Permalink to &quot;§ 6.2-2615 Suspension or revocation of license.&quot;">​</a></h1><p>A. The Commission may suspend or revoke any license issued under this chapter upon any of the following grounds:</p><ol><li><p>Any ground for denial of a license under this chapter;</p></li><li><p>Any violation of the provisions of this chapter or regulations adopted by the Commission pursuant thereto, or a violation of any other law or regulation applicable to the conduct of the licensee&#39;s business;</p></li><li><p>A course of conduct consisting of the failure to perform written agreements with qualified education loan borrowers;</p></li><li><p>Failure to account for funds received or disbursed to the satisfaction of the person supplying or receiving qualified education loan funds;</p></li><li><p>Conviction of any felony or of a misdemeanor involving fraud, misrepresentation, or deceit;</p></li><li><p>Entry of a judgment against the licensee involving fraud, misrepresentation, or deceit;</p></li><li><p>Entry of a federal or state administrative order against the licensee for violation of any law or regulation applicable to the conduct of the licensee&#39;s business;</p></li><li><p>Refusal to permit an investigation or examination by the Commission;</p></li><li><p>Failure to pay any fee or assessment imposed by this chapter; or</p></li><li><p>Failure to comply with any order of the Commission.</p></li></ol><p>B. For the purposes of this section, acts of any senior officer, director, member, partner, trustee, or principal shall be deemed acts of the licensee.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1198" target="_blank" rel="noreferrer">1198</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1250" target="_blank" rel="noreferrer">1250</a>.</p>',5),a=[r];function s(l,c,p,f,d,u){return o(),i("div",null,a)}const _=e(n,[["render",s]]);export{m as __pageData,_ as default};
