import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-2616 Notice of proposed suspension or revocation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-2616.md","filePath":"vacode/6.2-2616.md"}'),i={name:"vacode/6.2-2616.md"},a=n('<h1 id="§-6-2-2616-notice-of-proposed-suspension-or-revocation" tabindex="-1">§ 6.2-2616 Notice of proposed suspension or revocation. <a class="header-anchor" href="#§-6-2-2616-notice-of-proposed-suspension-or-revocation" aria-label="Permalink to &quot;§ 6.2-2616 Notice of proposed suspension or revocation.&quot;">​</a></h1><p>The Commission shall not revoke or suspend the license of any licensee upon any of the grounds set forth in § <a href="/vacode/6.2-2615/">6.2-2615</a> until it has given the licensee (i) 21 days&#39; notice in writing of the reasons for the proposed revocation or suspension and (ii) an opportunity to introduce evidence and be heard. The notice shall be sent by certified mail to the principal place of business of such licensee and shall state with particularity the grounds for the contemplated action. Within 14 days of mailing the notice, the licensee named therein may file with the Clerk of the Commission a written request for a hearing. If a hearing is requested, the Commission shall not suspend or revoke the license except based upon findings made at such hearing. The hearing shall be conducted in accordance with the Commission&#39;s Rules.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1198" target="_blank" rel="noreferrer">1198</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1250" target="_blank" rel="noreferrer">1250</a>.</p>',3),s=[a];function r(c,h,l,p,d,f){return o(),t("div",null,s)}const m=e(i,[["render",r]]);export{_ as __pageData,m as default};
